import fs from 'fs'

async function mkDist() {
	try { await fs.promises.mkdir('./dist') } catch (e) { if (e.code !== 'EEXIST') throw e }
}

await fs.promises.cp('./src', './dist', {
	recursive: true,
	filter: (src) => src.endsWith('.tsx') || (src.endsWith('.ts') && !src.endsWith('.d.ts')) ? false : true
})
